);\r\nvar sqs = new AWS.SQS({region : \'us-west-2\'});\r\nvar QUEUE_URL_DEAD_LETTER = \'https://sqs.us-west-2.amazonaws.com/---------/DEV_magento_post_orders_DEAD_LETTER_QUEUE\';\r\nvar QUEUE_URL = \'https://sqs.us-west-2.amazonaws.com/----------/DEV_magento_post_orders\';\r\n\r\n//***********************************************************\r\n// Axios Library\r\n//***********************************************************\r\nconst axios = require(\'axios\');\r\n\r\n//***********************************************************\r\n// Body\r\n//***********************************************************\r\nvar body;\r\n\r\n//***********************************************************\r\n// Event Handler\r\n//***********************************************************\r\n\r\nexports.handler = async (event, context) =&gt; {\r\n//**********************************\r\n// Loop to retrieve only 1 record from QUEUE\r\n// because I setup the SQS Batch size: 1\r\n//**********************************\r\nevent.Records.forEach(record =&gt; {\r\nbody = record;\r\n\r\n});\r\n\r\n//***********************************************************\r\n// POST TO MAPI OST API Order - v1/processOrdersWithPayload\r\n//***********************************************************\r\ntry {\r\n//----------------------------------\r\n// CLOUD WATCH\r\n//----------------------------------\r\nvar myJson = JSON.parse(body.body)\r\nconsole.log(\'JSON PAYLOAD - FROM SQS \' + JSON.stringify(body));\r\nconsole.log(\'THE WHOLE body.body \' + body.body);\r\n\r\n//----------------------------------\r\n// POST TO MAPI\r\n//----------------------------------\r\n/* GOOD POSITIVE TEST */\r\nconst res = await axios.post( hostMapi + \'/v1/processOrdersWithPayload\', myJson.data, {\r\nheaders: {\r\n\'Content-Type\': \'application/json\'\r\n}})\r\n\r\n//----------------------------------\r\n// CLOUD WATCH\r\n//----------------------------------\r\nconsole.log(\'SUCCESSFULLY POSTED TO MAPI v1/processOrdersWithPayload\');\r\nconsole.log(res.data.message)\r\n\r\n//----------------------------------\r\n// RESPONSE\r\n//----------------------------------\r\nreturn {\r\nstatusCode: 200,\r\nbody: res.data\r\n}\r\n} catch (err) {\r\n//----------------------------------\r\n// CLOUD WATCH - ERROR MESSAGE\r\n//----------------------------------\r\nconsole.log(\"FAILING PROCESSING \" + JSON.stringify(err));\r\n\r\n//----------------------------------\r\n// SQS DEAD-LETTER\r\n//----------------------------------\r\nawait pushSQSDeadLetter(sqs, JSON.stringify(err))\r\n\r\n//----------------------------------\r\n// SNS\r\n//----------------------------------\r\nawait pushSNS(\'ERROR \' , JSON.stringify(err))\r\nawait pushSNS(\'SQS PAYLOAD \' , JSON.stringify(body))\r\nawait pushSNS(\'JSON PAYLOAD \' , JSON.stringify(body.body))\r\n\r\n//----------------------------------\r\n// Return Response\r\n//----------------------------------\r\nreturn returnResponse(err);\r\n}\r\n}\r\n\r\nfunction returnResponse(err){\r\nreturn {\r\nstatusCode: 502,\r\nheaders: {\r\n\'Content-Type\': \'application/json\'\r\n},\r\nbody: err + JSON.stringify(body.body)\r\n}\r\n}\r\n\r\nfunction formatMessage(err, body){\r\nreturn \'POST FAILED TO MAPI /v1/processOrdersWithPayload\'\r\n+ \"\\n\\r &lt;br&gt; Error \" +\r\n+ err\r\n+ \"\\n\\r &lt;br&gt; \" +\r\n+ JSON.stringify(err)\r\n+ \"\\n\\r &lt;br&gt; JSON PAYLOAD\"\r\n+ body.body\r\n}\r\n\r\nfunction pushSQSDeadLetter(sqs, errorStr){\r\nlet params = {\r\nMessageAttributes: {\r\n\"Author\": {\r\nDataType: \"String\",\r\nStringValue: \"David Raleche\"\r\n},\r\n\"Error\": {\r\nDataType: \"String\",\r\nStringValue: errorStr\r\n},\r\n},\r\n\"MessageBody\": body.body ,\r\nQueueUrl: QUEUE_URL_DEAD_LETTER\r\n};\r\n\r\n//try {\r\n//async (result) =&gt; {\r\nlet sendingSQS = sqs.sendMessage(params).promise();\r\nsendingSQS.then(\r\nfunction(data) {\r\nconsole.log(\"SQS Message \"+ params.MessageBody + \" send sent to the SQS \"+ params.QueueUrl);\r\nconsole.log(\"SQS MessageID is \" + data.MessageId);\r\n}).catch(\r\nfunction(errsqs) {\r\nconsole.log(\"SQS ERROR SQS is \" + errsqs);\r\nconsole.error(errsqs, errsqs.stack);\r\n});\r\n//}\r\n// since your handler returns a promise, lambda will only resolve after sqs responded with either failure or success\r\n// console.log(\'SQS SUCCESSFULLY PUSHED data:\' + JSON.stringify(params));\r\n// } catch (err) {\r\n// console.log(\'SQS PUSH error : \',\"Fail Send Message\" + err);\r\n// }\r\n}\r\n\r\nfunction pushSNS(subject, message) {\r\nconsole.log(\"TRYING PUSHING SNS \"+ subject + message);\r\n// Create publish parameters\r\nvar paramsSNS = {\r\nSubject: subject,\r\nMessage: message, // required\r\nTopicArn: \'arn:aws:sns:us-west-2:045483043004:DEV-MAGENTO-ERROR\'\r\n};\r\n\r\n// Create promise and SNS service object\r\nvar publishTextPromise = new AWS.SNS({apiVersion: \'2010-03-31\'}).publish(paramsSNS).promise();\r\n\r\n// Handle promise\'s fulfilled/rejected states\r\npublishTextPromise.then(\r\nfunction(data) {\r\nconsole.log(\"SNS \"+subject + \" message \"+ paramsSNS.Message + \" send sent to the topic \"+ paramsSNS.TopicArn);\r\nconsole.log(\"SNS MessageID is \" + data.MessageId);\r\n}).catch(\r\nfunction(errr) {\r\nconsole.log(\"SNS ERROR SNS is \" + errr);\r\n\r\nconsole.error(errr, errr.stack);\r\n});\r\n\r\n}</blockquote>\r\n&nbsp;\r\n<h1>SNS - Slack Channel</h1>\r\n<blockquote>var https = require(\'https\');\r\nvar util = require(\'util\');\r\n\r\nexports.handler = function(event, context) {\r\nconsole.log(JSON.stringify(event, null, 2));\r\nconsole.log(\'From SNS:\', event.Records[0].Sns.Message);\r\n\r\nvar postData = {\r\n\"channel\": \"#dev-magento\",\r\n\"username\": \"AWS SNS via Lamda :: DevQa Cloud\",\r\n\"text\": \"*\" + event.Records[0].Sns.Subject + \"*\",\r\n\"icon_emoji\": \":aws:\"\r\n};\r\n\r\nvar message = event.Records[0].Sns.Message;\r\nvar severity = \"good\";\r\n\r\nvar dangerMessages = [\r\n\" but with errors\",\r\n\" to RED\",\r\n\"During an aborted deployme